public class MySnippet{

    public Renderer outer(){
        return Renderer.create("#innder", "now", new Date());
    }

    public Renderer inner(Date now){
        return Renderer.create("#current-date", now);
    }
}

public class MySnippet{

    public Renderer outer(){
        return Renderer.create("#innder", "now", new Date());
    }

    public Renderer embed(Date now){
        return Renderer.create("#current-date", now);
    }
}

